clcclear alld = 10 ; %in mm, the dia of tool ##ft = 0.03:0.01:0.07 %in mm##ae = [0.5*d:0.01:0.10*d];##ap = [0.5*d:0.01:2*d];##[ap, ae] = meshgrid(0.5*d:0.01:2*d, 0.5*d:0.01:0.10*d );[ae,ft] = meshgrid(0.1*d:0.04:0.5*d, 0.03:0.0004:0.07)figure(1)subplot(2,2,1)Ff = -202.59 + 4848.57*ft + 26*ae;contourf(ae, ft, Ff/1000)xlabel('RADIAL DOC IN mm')ylabel('FEED PER TOOTH [Ft]')zlabel('Force in Feed Direction in KN')title('FORCE IN FEED DIRECTION')colormap jet, shading interpcolorbarsubplot (2,2,2)Ffn = -66 + 9240*ft + 50*ae + 586.71*(ft-0.108)*(ae-12);contourf(ae, ft, Ffn/1000)title('Force in Normal to Feed Direction' )xlabel('RADIAL DOC IN mm')ylabel('FEED PER TOOTH [Ft]')zlabel('Force in normal to Feed Direction in KN')colormap jet,shading interpcolorbarsubplot (2,2,3)Fa = -58.95 + 5652.38*ft + 15.62*ae + 295.71*(ft-0.108)*(ae-12);contourf(ae, ft, Fa/1000)xlabel('RADIAL DOC IN mm')ylabel('FEED PER TOOTH [Ft]')zlabel('Axial Force in KN')title('AXIAL FORCE ON TOOL')colormap jet,shading interpcolorbarsubplot (2,2,4)fnet = sqrt(Ff.^2 + Ffn.^2+ Fa.^2);contourf(ae, ft, fnet/1000)xlabel('RADIAL DOC IN mm')ylabel('FEED PER TOOTH [Ft]')zlabel('NET FORCE ON TOOL IN KN')title('Net Force on Tool Vs RDOC')colormap jet,shading interpcolorbarm = min(fnet(:));n = max(fnet(:));t = (n-m)/100;ae = [1:0.04:5];ap = [1:0.19:20];figure (2)work = cumtrapz(ae,cumtrapz(ap,fnet));subplot(1,2,1)surf(ap,ae,work)colormap jet, shading interpxlabel('[ADOC]')ylabel('[RDOC]')zlabel('WORK')title('W0RK OUTPUT OF TOOL')colorbarsubplot(1,2,2)surf(ap,ae,work,50)xlabel('[ADOC]')ylabel('[RDOC]')grid ontitle('W0RK OUTPUT OF TOOL')colormap hsvcolorbar